function delay(time, random = 0) {
    return new Promise(function (resolve) {
        setTimeout(resolve, time + parseInt(Math.random() * random));
    });
}

new Promise(async (resolve) => {
    function createIframe(id, url) {
        return "<iFRAME SRC=\"" + url + "\" name=\"" + id + "\" width=\"840\" height=\"500\"></iframe>";
    }

    function setTroop(name, troop, quantity) {
        return `window.frames["${name}"].document.getElementById("unit_input_${troop}").value = ${quantity};`
    }

    function targetAttack(name) {
        return "window.frames['" + name + "'].document.getElementById('target_attack').click ();";
    }

    function sendAttack(name) {
        return "window.frames['" + name + "'].document.getElementById('troop_confirm_go').click ();";
    }

    var url = window.location.href;
    var iframes = "";
    var setTroops = "";
    var targetAttacks = []
    var sendAttacks = "";
    for (i = 1; i <= 5; i++) {
        iframes += createIframe('name' + i, url);
        setTroops += setTroop(`name${i}`, IDLE.troop, IDLE.quantity)
        targetAttacks.push(targetAttack('name' + i));
        sendAttacks += sendAttack('name' + i);
    }

    document.body.innerHTML = iframes

    await delay(3000)
    eval(setTroops)

    for (let i = 0; i < targetAttacks.length; i++) {
        await delay(2000, 2000)
        eval(targetAttacks[i])
    }

    await delay(5000)
    eval(sendAttacks)

    resolve()
})
