steps.script:
new Promise((resolve) => {

    var win = (window.frames.length > 0) ? window.main : window;
    var data = win.game_data;
    var qtdAtk = $('#incomings_amount').text();
    var qtdApo = $('#supports_amount').text();
    var text = data.player.name + ' - ' + data.world + '%0A%0AAtaques: ' + qtdAtk + '%0AApoios: ' + qtdApo + '%0A%0A'
    if (qtdAtk > 0) {
        $.get('https://api.telegram.org/bot'+IDLE.token+'/sendMessage?text=' + text + '&chat_id='+IDLE.chat_id).
          always(function() {
            resolve()
          });
    } else {
        resolve()
    }
})
